<template>
  <view class="empty-content"  :style="{top:isTop,position:position,margin:margin}">
    <image class="empty-content-image" :src="setSrc" mode="aspectFit"></image>
    <text>{{slotText}}</text>
    <!-- <slot></slot> -->
  </view>
</template>

<script>
export default {
  props: {
    src: {
      type: String,
      default: 'empty'
    },
    position:{
      type: String,
      default: ''
    },
    margin:{
      type: String,
      default: ''
    },
    isTop:{
      type: String,
      default: '35%'
    }
  },
  data() {
    return {
      typeSrc: {
        empty:
          'data:image/png;base64,'
      },
	  slotText: "暂无内容"
    };
  },
  onLoad() {},
  computed: {
    setSrc() {
      return this.typeSrc[this.src];
    }
  }
};
</script>

